// variables
var carrito = document.querySelector("#carrito-icono ");
var contFinalizar = document.querySelector("#contFinalizar"); // Evento que se dispara cuando se hace scroll

window.addEventListener("scroll", function () {
  var posicion = window.scrollY;
  contFinalizar.classList.remove("mostrarFinalizar");
  carrito.classList.remove("mostrarCarrito"); // Muestra el carrito según la posición en la pantalla

  if (posicion > 600) {
    carrito.classList.add("mostrarCarrito");
  }
  /* // Muestra cartel finalizar compra según la posición en la pantalla
